    private static boolean isValidUrl(String url) {
        try {
            new URL(url); // Verifică dacă URL-ul este valid
            return url.startsWith("http://") || url.startsWith("https://");
        } catch (MalformedURLException e) {
            return false; // URL-ul nu este valid
        }
    }

    private static boolean containsRequiredImages(String url) throws IOException {
        Document doc;
        try {
            doc = Jsoup.connect(url).get();
        } catch (IOException e) {
            System.err.println("Eroare la conectarea la URL-ul " + url + ": " + e.getMessage());
            return false;
        }

        Elements images = doc.select("img[src]");

        for (Element img : images) {
            String imgSrc = img.absUrl("src");
            if (IMAGE_URLS.contains(imgSrc)) {
                return true;
            }
        }
        return false;
    }
